import sys, bisect

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    pairs = [(int(next(it)), int(next(it))) for _ in range(n)]

    # Sort: increasing a, and for equal a decreasing b
    pairs.sort(key=lambda x: (x[0], -x[1]))

    tails = []  # tails[i] = smallest possible ending b of length i+1 chain
    for _, b in pairs:
        idx = bisect.bisect_left(tails, b)
        if idx == len(tails):
            tails.append(b)
        else:
            tails[idx] = b
    print(len(tails))

